// Á¢∫‰øù items ÊòØÂê¶ÊúâÂÖßÂÆπ
if (!items || items.length === 0) {
  console.log('No items found');
  return [];
}

// ÂèñÂæóÊâÄÊúâË≤ºÊñá
const posts = items.map(item => item.json).flat();
console.log('Number of posts:', posts.length);

// ÂèñÂæóÁ¨¨‰∏ÄÁØáË≤ºÊñáÁöÑÂü∫Êú¨Ë≥áË®ä
const firstPost = posts[0] || {};

// ÁîüÊàê blocks ÁµêÊßã
const blocks = [];

// ËôïÁêÜÊØèÂÄãË≤ºÊñá
posts.forEach(post => {
  // Â¶ÇÊûúÊúâÊñáÂ≠óÔºåÂä†ÂÖ•ÊñáÂ≠óÂçÄÂ°ä
  if (post.text) {
    blocks.push({
      object: "block",
      type: "paragraph",
      paragraph: {
        rich_text: [{
          type: "text",
          text: { content: post.text }
        }]
      }
    });
  }

  // Â¶ÇÊûúÊúâÂ™íÈ´îÈÄ£ÁµêÔºåÂä†ÂÖ• embed ÂçÄÂ°ä
  if (post.media_urls && post.media_urls.length > 0) {
    post.media_urls.forEach(url => {
      blocks.push({
        object: "block",
        type: "embed",
        embed: { url }
      });
    });
  }
});

// Âêà‰ΩµÂü∫Êú¨Ë≥áË®äÂíå blocks
const combinedPost = {
  id: firstPost.id || '',
  permalink: firstPost.permalink || '',
  username: firstPost.username || '',
  timestamp: firstPost.timestamp || '',
  blocks
};

// Â∞áÁµêÊûúËΩâÊèõÁÇ∫ n8n ÊâÄÈúÄÊ†ºÂºè
return [{ json: combinedPost }];

