// Create our Slack message
// This will output a list of Ticket URLs with the status and the subject
// 12345 [STATUS] - Ticket Subject
let message = "*Unassigned Tickets*\n\n";

// Loop the input items
for (item of items) {
  // Append the ticket information to the message
  message += "*<" + item.json.url.replace("api/v2","agent").replace(".json","") + "|" + item.json.id + ">* [" + item.json.status.toUpperCase() + "] - " + item.json.subject + "\n"; 
}

// Return our message
return [{json: {message}}];
